import {Dictionary, DictionaryElements} from "./Dictionary.mjs";


function TLanguage(lang) {
  if (lang === "No") {
    return [Dictionary.no, DictionaryElements.no];
  } else {
    return [Dictionary.en, DictionaryElements.en];
  }
}

function changeButtons(dict) {
  for (let i = 0; i < elements.length; i++) {
    for (const key in dict) {
      if (elements[i].id === key) {
        elements[i].innerHTML = dict[key];
      }
    }
  }
}

export default TLanguage;
